// --- Parser Alexandra Farms (Orden de Compra PDF) ---
// Entrada: JSON con campos remitente, subject, empresa, dominio, tipoDeArchivo, contenido
// Salida: Objeto normalizado con datos clave para flujo Biologix

const input = $json;
const texto = input.contenido || "";

// --- Funciones auxiliares ---
function limpiarNumero(valor) {
  if (!valor) return 0;
  const num = String(valor).replace(/[^\d]/g, "");
  return num ? parseInt(num, 10) : 0;
}

function limpiarTexto(valor) {
  return valor ? valor.replace(/\s+/g, " ").trim() : "";
}

// --- Extracci√≥n de datos principales ---
const numeroOrden = texto.match(/\b\d{4,6}\b/)?.[0] || "N/A";
const fechaCompra = texto.match(/Fecha\s+(\d{1,2}[-/][a-zA-Z]{3,9}[-/]\d{2,4})/i)?.[1]?.replace(/[-]/g, "/") || "";
const fechaEntrega = texto.match(/Fecha\s+de\s+Entrega\s+(\d{1,2}[-/][a-zA-Z]{3,9}[-/]\d{2,4})/i)?.[1]?.replace(/[-]/g, "/") || "";

// --- Producto principal ---
const productoMatch = texto.match(/Precision\s+MD\s+([\d,.]+)\s+([\d,.]+)[^\d]+([\d,.]+)/i);

let cantidad = 0;
let valorUnitario = 0;
let subtotal = 0;

if (productoMatch) {
  cantidad = parseFloat(productoMatch[1].replace(",", ".")) || 0;
  valorUnitario = limpiarNumero(productoMatch[2]);
  subtotal = limpiarNumero(productoMatch[3]);
}

// --- Construcci√≥n de estructura final ---
const salida = {
  remitente: input.remitente || "",
  empresa: input.empresa || "Alexandra Farms",
  dominio: input.dominio || "",
  tipoDeArchivo: input.tipoDeArchivo || "pdf",
  numeroOrden,
  fechaCompra,
  fechaEntrega,
  items: [
    {
      itemID: "PRECISION MD 4L",
      descripcion: "PRECISION MD",
      cantidad,
      unidad: "LITRO",
      valorUnitario,
      subtotal,
      iva: 0,
      total: subtotal,
    }
  ],
  subtotalGeneral: subtotal,
  totalGeneral: subtotal,
  observaciones: "Orden procesada autom√°ticamente.",
  fuente: "Parser Alexandra Farms",
  estadoDeteccion: "ok"
};

return salida;
