const items = $input.all();

/**
 * Detectar item de metadatos:
 * - viene de ExtraerMetaDatos (y ahora tambi√©n incluye tipo_archivo)
 * - normalmente trae headers + threadId + text
 */
const metaItem =
  items.find(i => i.json && i.json.headers && (i.json.threadId || i.json.text || i.json.headers.from)) ||
  items.find(i => i.json && i.json.headers); // fallback

// Si no hay metadatos, igual no dejamos romper el flujo
const meta = metaItem?.json || {};

// Tipo de archivo (NO lo inventamos, lo traemos del metadato)
const tipo_archivo =
  meta.tipo_archivo ||
  meta.attributes?.tipo_archivo ||
  "DESCONOCIDO";

// Armar bloque correo (metadatos que necesitas para el switch empresarial)
const correo = {
  from: meta.headers?.from || meta.from || "",
  subject: meta.headers?.subject || meta.subject || "",
  threadId: meta.threadId || meta.messageId || "",
  text: meta.text || ""
};

// Todo lo que NO sea el item de metadatos es orden (XLS con 1 o N filas, o PDF estructurado)
const ordenItems = metaItem
  ? items.filter(i => i !== metaItem)
  : items;

// Normalizar a array de objetos JSON
const orden_raw = ordenItems.map(i => i.json);

// Output est√°ndar
return [
  {
    json: {
      correo,
      tipo_archivo,
      orden_raw
    }
  }
];

const input = $input.first().json;

const correo = input.correo;
const filas = input.orden_raw || [];

/* =========================
   Helpers
========================= */

// Excel date ‚Üí JS Date
function excelDateToJS(serial) {
  const utc_days = Math.floor(serial - 25569);
  const utc_value = utc_days * 86400;
  return new Date(utc_value * 1000);
}

// Format date DD/MM/YYYY
function formatDate(date) {
  const d = String(date.getDate()).padStart(2, "0");
  const m = String(date.getMonth() + 1).padStart(2, "0");
  const y = date.getFullYear();
  return `${d}/${m}/${y}`;
}

// Next Friday after date
function nextFriday(date) {
  const d = new Date(date);
  const day = d.getDay(); // 0 Sun ... 5 Fri
  const diff = (5 - day + 7) % 7 || 7;
  d.setDate(d.getDate() + diff);
  return d;
}

// Extract finca name from Lugar Entrega
function extractFinca(lugar) {
  if (!lugar) return "FINCA SIN NOMBRE";

  // Caso: "Finca La Esperanza"
  const fincaMatch = lugar.match(/Finca\s+([^-,]+)/i);
  if (fincaMatch) return fincaMatch[1].trim().toUpperCase();

  // Caso: √∫ltimo segmento "- ATLAS"
  const parts = lugar.split("-");
  if (parts.length > 1) {
    return parts[parts.length - 1].trim().toUpperCase();
  }

  return lugar.trim().toUpperCase();
}

/* =========================
   Agrupar por Orden
========================= */

const ordenes = {};

for (const row of filas) {
  const numeroOrden = row["Orden Compra"];
  const empresa = row["Empresa"];

  if (!ordenes[numeroOrden]) {
    const fechaCompraJS = excelDateToJS(row["Fecha Elaborado"]);
    const fechaEntregaJS = nextFriday(fechaCompraJS);

    ordenes[numeroOrden] = {
      numeroOrden,
      empresa,
      fechaCompra: formatDate(fechaCompraJS),
      fechaEntrega: formatDate(fechaEntregaJS),
      fincas: {},
      subtotalGeneral: 0
    };
  }

  const orden = ordenes[numeroOrden];

  const fincaNombre = extractFinca(row["Lugar Entrega"]);

  if (!orden.fincas[fincaNombre]) {
    orden.fincas[fincaNombre] = [];
  }

  const item = {
    producto: row["Nombre Articulo"],
    cantidad: row["Cantidad Equivalente"] || row["Cantidad Compra"],
    valorUnitario: row["Vlr Und Equiv"] || row["Vlr Und Compra"],
    valorTotal: row["Subtotal"],
    bodega: row["Bodega"]
  };

  orden.fincas[fincaNombre].push(item);
  orden.subtotalGeneral += Number(row["Subtotal"] || 0);
}

/* =========================
   Salida final (1 por orden)
========================= */

return Object.values(ordenes).map(o => ({
  json: {
    grupo_empresarial: 'GRUPO CHIA',
    remitente: correo.from,
    subject: correo.subject,
    empresa: o.empresa,
    json_estructurado: {
      numeroOrden: o.numeroOrden,
      fechaCompra: o.fechaCompra,
      fechaEntrega: o.fechaEntrega,
      empresa: o.empresa,
      finca: o.fincas,
      subtotalGeneral: o.subtotalGeneral

    }
  }
}));

const item = $input.first();

// Buscar cualquier attachment
const binaryKeys = Object.keys(item.binary || {});
if (binaryKeys.length === 0) {
  return [{ json: { ...item.json, tipo_archivo: "SIN_ARCHIVO" } }];
}

const bin = item.binary[binaryKeys[0]];

let tipo_archivo = "DESCONOCIDO";

if (bin.mimeType?.includes("pdf")) {
  tipo_archivo = "PDF";
} else if (
  bin.mimeType?.includes("excel") ||
  bin.fileExtension?.toLowerCase() === "xls" ||
  bin.fileExtension?.toLowerCase() === "xlsx"
) {
  tipo_archivo = "XLS";
}

return [
  {
    json: {
      ...item.json,
      tipo_archivo
    },
    binary: item.binary
  }
];

// --- Validaci√≥n de cantidades y precios (Elite Flowers) ---
// Compatible con varias l√≠neas por finca

const data = $json;
let verificacion = "ok";

// Precio oficial Grupo CHIA
const PRECIO_CHIA = 191703;

const fincas = data.json_estructurado?.finca || {};

let errores = [];

if (Object.keys(fincas).length === 0) {
  verificacion = "error";
  errores.push({
    finca: null,
    tipo: "estructura",
    mensaje: "No se encontraron fincas en la orden."
  });
} else {

  for (const finca in fincas) {

    const lineas = fincas[finca]; // ‚Üê ahora es un array

    for (const linea of lineas) {

      const cantidad = Number(linea.cantidad) || 0;
      const precioUnitario = Number(linea.valorUnitario) || 0;

      // --- Validaci√≥n 1: Cantidad m√∫ltiplo de 4 ---
      if (cantidad % 4 !== 0) {
        verificacion = "error";
        errores.push({
          finca,
          tipo: "cantidad",
          valor_recibido: cantidad,
          valor_esperado: "M√∫ltiplo de 4",
          sugerencia: Math.ceil(cantidad / 4) * 4
        });
      }

      // --- Validaci√≥n 2: Precio correcto ---
      if (precioUnitario !== PRECIO_CHIA) {
        verificacion = "error";
        errores.push({
          finca,
          tipo: "precio",
          valor_recibido: precioUnitario,
          valor_esperado: PRECIO_CHIA
        });
      }

    }
  }
}

return [
  {
    ...data,
    verificacion,
    errores_validacion: errores
  }
];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

const rows = $input.all();

// Supabase devuelve 1 item vac√≠o {} cuando no hay resultados
const existeOrden =
  rows.length === 1 &&
  rows[0].json &&
  rows[0].json.id; // üëà campo REAL de la tabla

return [
  {
    json: {
      orden_duplicada: Boolean(existeOrden),
      orden_existente: existeOrden ? rows[0].json : null
    }
  }
];

// --- Unificar informaci√≥n proveniente de un Merge ---
// Entrada esperada: m√∫ltiples items (ej: validaci√≥n duplicada + orden estructurada)
// Salida: UN solo item con toda la info consolidada

const items = $input.all();

let resultado = {
  orden_duplicada: false,
  orden_existente: null,
  grupo_empresarial: null,
  remitente: null,
  subject: null,
  empresa: null,
  json_estructurado: null,
};

// Recorremos todos los items del merge
for (const item of items) {
  const data = item.json;

  // Bloque validaci√≥n duplicada (Supabase / DB)
  if (data.hasOwnProperty("orden_duplicada")) {
    resultado.orden_duplicada = data.orden_duplicada;
    resultado.orden_existente = data.orden_existente ?? null;
  }

  // Bloque informaci√≥n de la orden
  if (data.json_estructurado) {
    resultado.grupo_empresarial = data.grupo_empresarial ?? null;
    resultado.remitente = data.remitente ?? null;
    resultado.subject = data.subject ?? null;
    resultado.empresa = data.empresa ?? data.json_estructurado.empresa ?? null;
    resultado.json_estructurado = data.json_estructurado;
  }
}

// ‚ö†Ô∏è Garantizar SIEMPRE salida para que el flujo contin√∫e
return [
  {
    json: resultado,
  },
];

// ---------- Utilidad: DD/MM/YYYY ‚Üí YYYY-MM-DD ----------
function normalizarFecha(fecha) {
  if (!fecha || typeof fecha !== "string") return null;

  const partes = fecha.split("/");
  if (partes.length !== 3) return null;

  const [dd, mm, yyyy] = partes;
  return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
}

const data = $json;
const infoOriginal = data.json_estructurado || {};

// ---------- Normalizar fechas ----------
const fechaCompraISO = normalizarFecha(infoOriginal.fechaCompra);
const fechaEntregaISO = normalizarFecha(infoOriginal.fechaEntrega);

// ---------- Clonar json_estructurado con fechas normalizadas ----------
const jsonEstructuradoNormalizado = {
  ...infoOriginal,
  fechaCompra: fechaCompraISO,
  fechaEntrega: fechaEntregaISO,
};

// ---------- Determinar estado (ENUM v√°lido) ----------
let estado = "RECIBIDA";
if (data.orden_duplicada === true) estado = "DUPLICADA";
if (data.verificacion === "error") estado = "INCONSISTENTE";

// ---------- Output final ----------
return [
  {
    json: {
      // Identidad
      grupo_empresarial: data.grupo_empresarial,
      empresa: data.empresa,
      orden_compra: jsonEstructuradoNormalizado.numeroOrden,

      // Fechas (ISO)
      fecha_compra: fechaCompraISO,
      fecha_entrega: fechaEntregaISO,

      // Valores
      moneda: "COP",
      subtotal_general: jsonEstructuradoNormalizado.subtotalGeneral || 0,

      // Estado (ENUM)
      estado,

      // Origen / comunicaci√≥n
      fuente: "EMAIL",
      remitente: data.remitente,
      subject: data.subject,
      thread_id: data.thread_id || null,

      // Auditor√≠a / Persistencia
      raw_input: {
        json_estructurado: jsonEstructuradoNormalizado,
        verificacion: data.verificacion,
        errores_validacion: data.errores_validacion,
      },
    },
  },
];

const items = $input.all();
const output = [];

for (const item of items) {
  const ordenId = item.json.id;
  const jsonEstructurado = item.json.raw_input?.json_estructurado;

  if (!ordenId || !jsonEstructurado?.finca) {
    throw new Error("No se encontr√≥ orden_id o finca en json_estructurado");
  }

  const fincas = jsonEstructurado.finca;

  for (const [nombreFinca, itemsFinca] of Object.entries(fincas)) {
    for (const it of itemsFinca) {
      output.push({
        json: {
          orden_id: ordenId,
          finca: nombreFinca,
          bodega: it.bodega || null,
          producto: it.producto || null,
          cod_articulo: it.cod_articulo || null,
          cantidad: Number(it.cantidad || 0),
          unidad: it.unidad || "LTS",
          valor_unitario: Number(it.valorUnitario || 0),
          valor_total: Number(it.valorTotal || 0),
          porc_iva: it.porc_iva || 0,
          subtotal: Number(it.valorTotal || 0),

          // Auditor√≠a
          raw_item: it
        }
      });
    }
  }
}

return output;

const item = $json;

return [
  {
    json: {
      orden_id: item.orden_id,
      tipo: "ORDEN_RECIBIDA",
      detalle: "Orden recibida correctamente y registrada en el sistema",

      payload: {
        estado: "RECIBIDA",
        empresa: item.empresa,
        grupo_empresarial: item.grupo_empresarial,
        fecha_compra: item.fecha_compra,
        fecha_entrega: item.fecha_entrega,
        subtotal_general: item.subtotal_general,
        moneda: item.moneda,
        fuente: item.fuente,

        // Snapshot t√©cnico
        raw_input: item.raw_input
      }
    }
  }
];

// --- n8n Function: OrganizarInfoPDFGRCHIA ---

const correo = $json.correo || {};
const raw = ($json.orden_raw?.[0]?.text || "").toString();

// ---------------- Normalizaci√≥n ----------------
let T = raw.replace(/\r/g, "").replace(/[ \t]+/g, " ").trim();
const T1 = T.replace(/\n/g, " ");

// ---------------- Helpers ----------------
const MESES = {
  enero:"01", febrero:"02", marzo:"03", abril:"04", mayo:"05", junio:"06",
  julio:"07", agosto:"08", septiembre:"09", setiembre:"09",
  octubre:"10", noviembre:"11", diciembre:"12"
};

const cap = (re, src = T) => {
  const m = src.match(re);
  return m ? (m[1] || m[0]).trim() : "";
};

const toISO = (s) => {
  if (!s) return "";
  const m = s.match(/(\d{1,2})\s+([A-Za-z√Å√â√ç√ì√ö√ë√°√©√≠√≥√∫√±]+)\s+(\d{4})/);
  if (!m) return "";
  const d = m[1].padStart(2, "0");
  const mm = MESES[m[2].toLowerCase()] || "";
  return mm ? `${m[3]}-${mm}-${d}` : "";
};

const nextFridayISO = (iso) => {
  if (!iso) return "";
  const d = new Date(iso + "T00:00:00");
  const delta = (5 - d.getDay() + 7) % 7 || 7;
  d.setDate(d.getDate() + delta);
  return d.toISOString().slice(0, 10);
};

const parseMoneyCOP = (s) => {
  if (!s) return 0;
  const clean = s.replace(/,/g, "");
  return Math.round(parseFloat(clean)) || 0;
};

// ---------------- Cabecera ----------------
const empresa = "MG CONSULTORES S.A.S.";
const noOrden = cap(/No\.?\s*(\d{5,})/i, T);
const numeroOrden = noOrden ? `GM - ${noOrden}` : "N/A";

const fechas = T.match(/\d{1,2}\s+(?:[A-Za-z]+)\s+\d{4}/gi) || [];
const fechaCompraISO = toISO(fechas[0] || "");
const fechaEntregaISO = nextFridayISO(fechaCompraISO);

const fincaNombre = cap(/Finca\s+([A-Za-z√Å√â√ç√ì√ö√ë√°√©√≠√≥√∫√± ]+?)(?:-|,|‚Äî)/i, T1);
const fincaKey = fincaNombre ? fincaNombre.trim().toUpperCase() : "SIN FINCA";

// ---------------- Extracci√≥n Multi-√çtem ----------------
const items = [];
let subtotalGeneral = 0;

// Usamos el flag 'g' (global) para encontrar todos los productos
const productRegex = /(PRECISION\s*MD)[\s\S]*?(\d+\.\d{2})\s+([\d,.]+\.\d{2})\s+([\d,.]+\.\d{2})/gi;

let match;
while ((match = productRegex.exec(T1)) !== null) {
    const nombreProducto = match[1].trim();
    const cantidad = parseFloat(match[2]);
    const total = parseMoneyCOP(match[4]);

    // Divisi√≥n para obtener el unitario de cada √≠tem detectado
    const unitarioCalculado = cantidad > 0 ? Math.round(total / cantidad) : 0;

    items.push({
        producto: nombreProducto,
        cantidad: cantidad,
        valorUnitario: unitarioCalculado,
        valorTotal: total,
        bodega: ""
    });

    subtotalGeneral += total;
}

// Fallback si no encontr√≥ nada con el regex global
if (items.length === 0) {
    const monies = T1.match(/[\d,.]+\.\d{2}/g) || [];
    if (monies.length >= 2) {
        const totalVal = parseMoneyCOP(monies[monies.length - 1]);
        const unitarioVal = Math.round(totalVal / 5);
        items.push({
            producto: "PRECISION MD",
            cantidad: 5,
            valorUnitario: unitarioVal,
            valorTotal: totalVal,
            bodega: ""
        });
        subtotalGeneral = totalVal;
    }
}

// ---------------- Salida Final ----------------
const json_estructurado = {
  numeroOrden,
  fechaCompra: fechaCompraISO,
  fechaEntrega: fechaEntregaISO,
  empresa,
  finca: { [fincaKey]: items },
  subtotalGeneral
};

return [{
  json: {
    grupo_empresarial: "GRUPO CHIA",
    empresa,
    orden_compra: numeroOrden,
    fecha_compra: fechaCompraISO,
    fecha_entrega: fechaEntregaISO,
    moneda: "COP",
    subtotal_general: subtotalGeneral,
    estado: "RECIBIDA",
    fuente: "EMAIL",
    remitente: correo.from || "",
    subject: correo.subject || "",
    thread_id: correo.threadId || null,
    raw_input: {
      json_estructurado,
      tipo_archivo: "PDF",
      correo_texto: correo.text || ""
    }
  }
}];

// --- Validaci√≥n SOLO PDF Grupo CH√çA ---
// Regla:
// - NO validar cantidad
// - Precio correcto = valorUnitario / 4 === PRECIO_CHIA

const data = $json;

const PRECIO_CHIA = 191703;

let verificacion = "ok";
let errores = [];

const fincas = data.json_estructurado?.finca || {};

for (const finca in fincas) {
  const lineas = fincas[finca];

  for (const linea of lineas) {
    const valorUnitario = Number(linea.valorUnitario) || 0;

    if (!valorUnitario) {
      verificacion = "error";
      errores.push({
        finca,
        tipo: "precio",
        mensaje: "Valor unitario no encontrado"
      });
      continue;
    }

    const precioPorLitro = Math.round(valorUnitario / 4);

    if (precioPorLitro !== PRECIO_CHIA) {
      verificacion = "error";
      errores.push({
        finca,
        tipo: "precio",
        valor_recibido: precioPorLitro,
        valor_esperado: PRECIO_CHIA
      });
    }
  }
}

return [
  {
    ...data,
    verificacion,
    errores_validacion: errores
  }
];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

const rows = $input.all();

// Supabase devuelve 1 item vac√≠o {} cuando no hay resultados
const existeOrden =
  rows.length === 1 &&
  rows[0].json &&
  rows[0].json.id; // üëà campo REAL de la tabla

return [
  {
    json: {
      orden_duplicada: Boolean(existeOrden),
      orden_existente: existeOrden ? rows[0].json : null
    }
  }
];

// --- Unificar informaci√≥n proveniente de un Merge ---
// Soporta √≥rdenes Grupo Ch√≠a desde PDF y XLS
// Garantiza SIEMPRE un output v√°lido

const items = $input.all();

let resultado = {
  orden_duplicada: false,
  orden_existente: null,
  grupo_empresarial: null,
  remitente: null,
  subject: null,
  empresa: null,
  json_estructurado: null,
};

// Recorremos todos los items del merge
for (const item of items) {
  const data = item.json || {};

  // -------------------------------
  // 1Ô∏è‚É£ Bloque validaci√≥n duplicada
  // -------------------------------
  if (data.hasOwnProperty("orden_duplicada")) {
    resultado.orden_duplicada = data.orden_duplicada;
    resultado.orden_existente = data.orden_existente ?? null;
  }

  // ---------------------------------------
  // 2Ô∏è‚É£ Bloque ORDEN (XLS o PDF)
  // ---------------------------------------

  // Caso A: flujo XLS / normal
  if (data.json_estructurado) {
    resultado.grupo_empresarial = data.grupo_empresarial ?? resultado.grupo_empresarial;
    resultado.remitente = data.remitente ?? resultado.remitente;
    resultado.subject = data.subject ?? resultado.subject;
    resultado.empresa =
      data.empresa ??
      data.json_estructurado.empresa ??
      resultado.empresa;

    resultado.json_estructurado = data.json_estructurado;
  }

  // Caso B: flujo PDF (Grupo Ch√≠a)
  if (data.raw_input?.json_estructurado) {
    resultado.grupo_empresarial = data.grupo_empresarial ?? resultado.grupo_empresarial;
    resultado.remitente = data.remitente ?? resultado.remitente;
    resultado.subject = data.subject ?? resultado.subject;
    resultado.empresa =
      data.empresa ??
      data.raw_input.json_estructurado.empresa ??
      resultado.empresa;

    resultado.json_estructurado = data.raw_input.json_estructurado;
  }
}

// ---------------------------------------
// ‚ö†Ô∏è Protecci√≥n final (nunca romper flujo)
// ---------------------------------------
if (!resultado.json_estructurado) {
  resultado.json_estructurado = null;
}

// Salida √∫nica
return [
  {
    json: resultado,
  },
];

const items = $input.all();
const output = [];

for (const item of items) {
  const ordenId = item.json.id;
  const jsonEstructurado = item.json.raw_input?.json_estructurado;

  if (!ordenId || !jsonEstructurado?.finca) {
    throw new Error("No se encontr√≥ orden_id o finca en json_estructurado");
  }

  const fincas = jsonEstructurado.finca;

  for (const [nombreFinca, itemsFinca] of Object.entries(fincas)) {
    for (const it of itemsFinca) {

      // ---------------------------------------------
      // PDF GRUPO CHIA:
      // cantidad viene en GALONES
      // 1 gal√≥n = 4 litros
      // ---------------------------------------------
      const cantidadGalones = Number(it.cantidad || 0);
      const cantidadLitros = cantidadGalones * 4;

      output.push({
        json: {
          orden_id: ordenId,
          finca: nombreFinca,
          bodega: it.bodega || null,
          producto: it.producto || null,
          cod_articulo: it.cod_articulo || null,

          // üîë TRANSFORMACI√ìN CLAVE
          cantidad: cantidadLitros,
          unidad: "LTS",

          valor_unitario: Number(it.valorUnitario || 0),
          valor_total: Number(it.valorTotal || 0),
          porc_iva: it.porc_iva || 0,
          subtotal: Number(it.valorTotal || 0),

          // Auditor√≠a
          raw_item: {
            ...it,
            cantidad_galones: cantidadGalones,
            cantidad_litros: cantidadLitros
          }
        }
      });
    }
  }
}

return output;

// ---------- Utilidad: normalizar fecha ----------
function normalizarFecha(fecha) {
  if (!fecha || typeof fecha !== "string") return null;

  // ISO (YYYY-MM-DD)
  if (/^\d{4}-\d{2}-\d{2}$/.test(fecha)) {
    return fecha;
  }

  // DD/MM/YYYY
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(fecha)) {
