// --- Parser √ìrdenes de Compra Biologix (versi√≥n robusta definitiva) ---
// Admite 1 o varias fincas. Detecta total real y limpia formatos num√©ricos.
// Entrada est√°ndar proveniente del switch por empresa (misma estructura).

const input = $json;
const text = input.contenido || "";

// ------------------ FUNCIONES AUXILIARES ------------------

// Convierte strings de montos como "2,304,708.00" ‚Üí 2304708
function parseMoney(value) {
  if (!value) return 0;
  // elimina todo excepto d√≠gitos
  const cleaned = String(value).replace(/[^\d]/g, "");
  return cleaned ? Math.round(parseInt(cleaned, 10) / 100) : 0;
}

// Convierte cantidades como "8.00LT" o "12,00lt" ‚Üí 8 / 12
function parseQty(value) {
  if (!value) return 0;
  const numeric = String(value).replace(/[^\d.,]/g, "").replace(",", ".");
  return parseFloat(numeric) || 0;
}

// ------------------ EXTRACCI√ìN DE DATOS ------------------

// Empresa compradora: justo despu√©s de "Correo:"
const empresaCompradora =
  text.match(/Correo:\s*\n?([^\n]+)/i)?.[1]?.trim() ||
  input.empresa ||
  "";

// N√∫mero de orden
const numeroOrden = text.match(/ORDEN DE COMPRA\.\s*([A-Z0-9\-]+)/i)?.[1] || "";

// ------------------ FECHAS (solo DD/MM/YYYY, robusto) ------------------

let fechaCompra = "";
let fechaEntrega = "";

// Fecha: 1-2 d√≠gitos d√≠a, 1-2 mes, 4 a√±o
const regexFecha = /(\d{1,2}\/\d{1,2}\/\d{4})/;

// -------- Fecha de Compra --------
let compraMatch =
  text.match(new RegExp(regexFecha.source + "\\s*(?:\\d{1,2}:\\d{2}:\\d{2})?\\s*Fecha\\s*de\\s*Compra\\s*;?", "i")) ||
  text.match(new RegExp(regexFecha.source + "(?=\\s*Fecha\\s*de\\s*Compra)", "i"));

if (compraMatch) {
  fechaCompra = compraMatch[1]; // Extrae solo la fecha
}

// -------- Fecha de Entrega --------
let entregaMatch =
  text.match(new RegExp(regexFecha.source + "\\s*(?:\\d{1,2}:\\d{2}:\\d{2})?\\s*Fecha\\s*de\\s*Entrega\\s*;?", "i")) ||
  text.match(new RegExp(regexFecha.source + "(?=\\s*Fecha\\s*de\\s*Entrega)", "i"));

if (entregaMatch) {
  fechaEntrega = entregaMatch[1];
}



// ------------------ SECCI√ìN DE PRODUCTOS ------------------

// Secci√≥n despu√©s de "Id Item"
const body = text.split(/Id Item/i)[1] || "";

// L√≠neas de producto PRECISION MD X 4LT
const regexLine =
  /PRECISION\s+MD\s+X\s+4LT\s+([\d.,]+)\s*[Ll][Tt]\s+([\d.,]+)\s+([\d.,]+)\s+[0.,]+\s+([A-Z√Å√â√ç√ì√ö√ú√ë .\-\/]+)/g;

const finca = {};
for (const m of body.matchAll(regexLine)) {
  const cantidad = parseQty(m[1]);
  const valorUnitario = parseMoney(m[2]);
  const valorTotal = parseMoney(m[3]);
  const nombreFinca = m[4].trim();

  if (!finca[nombreFinca]) {
    finca[nombreFinca] = [];
  }

  finca[nombreFinca].push({
    producto: "PRECISION MD X 4LT",
    cantidad,
    valorUnitario,
    valorTotal,
  });
}


// Total sin IVA (despu√©s de "Valor IVA:")
const totalMatch = text.match(/Valor IVA:\s*\n([\d.,]+)/i);
let subtotalGeneral = totalMatch ? parseMoney(totalMatch[1]) : 0;

// Si no se encontr√≥ el total expl√≠cito, calcula la suma
if (!subtotalGeneral) {
  subtotalGeneral = Object.values(finca).reduce(
    (acc, f) => acc + (f.valorTotal || 0),
    0
  );
}

// ------------------ SALIDA FINAL ------------------
return [
  {
    json: {
      remitente: input.remitente || "",
      subject: input.subject || "",
      empresa: empresaCompradora,
      json_estructurado: {
        numeroOrden,
        fechaCompra,
        fechaEntrega,
        empresa: empresaCompradora,
        finca,
        subtotalGeneral,
      },
    },
  },
];

