// -----------------------------------------------------------------------------
// -------- FUNCI√ìN ROBUSTA: SIGUIENTE VIERNES DESDE FECHA COMPRA ----------------
// -----------------------------------------------------------------------------

function obtenerSiguienteViernes(fechaBase) {
  if (!fechaBase) return "Sin definir";

  const texto = String(fechaBase);

  // Extrae DD/MM/YYYY desde cualquier texto
  const match = texto.match(/(\d{2})\/(\d{2})\/(\d{4})/);

  let fecha;

  if (match) {
    const [, dia, mes, anio] = match;
    fecha = new Date(`${anio}-${mes}-${dia}`);
  } else {
    fecha = new Date(texto);
  }

  if (isNaN(fecha.getTime())) return "Sin definir";

  const VIERNES = 5;
  const diaSemana = fecha.getDay();

  let diasHastaViernes = (VIERNES - diaSemana + 7) % 7;
  if (diasHastaViernes === 0) diasHastaViernes = 7;

  fecha.setDate(fecha.getDate() + diasHastaViernes);

  const dd = String(fecha.getDate()).padStart(2, "0");
  const mm = String(fecha.getMonth() + 1).padStart(2, "0");
  const yyyy = fecha.getFullYear();

  return `${dd}/${mm}/${yyyy}`;
}

