function obtenerSiguienteViernes(fechaBase) {
  if (!fechaBase) return "";

  let fecha;

  // Soporta DD/MM/YYYY
  if (typeof fechaBase === "string" && fechaBase.includes("/")) {
    const [dia, mes, anio] = fechaBase.split("/");
    fecha = new Date(`${anio}-${mes}-${dia}`);
  } else {
    fecha = new Date(fechaBase);
  }

  if (isNaN(fecha.getTime())) return "";

  const VIERNES = 5; // 0=Domingo, 5=Viernes
  const diaSemana = fecha.getDay();

  let diasHastaViernes = (VIERNES - diaSemana + 7) % 7;

  // Si ya es viernes ‚Üí siguiente viernes
  if (diasHastaViernes === 0) {
    diasHastaViernes = 7;
  }

  fecha.setDate(fecha.getDate() + diasHastaViernes);

  // Devolver DD/MM/YYYY
  const dd = String(fecha.getDate()).padStart(2, "0");
  const mm = String(fecha.getMonth() + 1).padStart(2, "0");
  const yyyy = fecha.getFullYear();

  return `${dd}/${mm}/${yyyy}`;
}

